function AnimatedObject(){this.init()}AnimatedObject.prototype.init=function(){this.backgroundColor="#FFFFFF";this.foregroundColor="#000000";this.highlighted=false;this.objectID=-1;this.layer=0;this.addedToScene=true;this.label="";this.labelColor="#000000";this.alpha=1;this.x=0;this.y=0;this.minHeightDiff=3;this.range=5};AnimatedObject.prototype.setBackgroundColor=function(e){this.backgroundColor=e};AnimatedObject.prototype.setForegroundColor=function(e){this.foregroundColor=e};AnimatedObject.prototype.setNull=function(){};AnimatedObject.prototype.getNull=function(){return false};AnimatedObject.prototype.setAlpha=function(e){this.alpha=e};AnimatedObject.prototype.getAlpha=function(){return this.alpha};AnimatedObject.prototype.setForegroundColor=function(e){this.foregroundColor=e;this.labelColor=e};AnimatedObject.prototype.getHighlight=function(){return this.highlighted};AnimatedObject.prototype.getWidth=function(){return 0};AnimatedObject.prototype.setHighlight=function(e){this.highlighted=e};AnimatedObject.prototype.centerX=function(){return this.x};AnimatedObject.prototype.centerY=function(){return this.y};AnimatedObject.prototype.alignLeft=function(e){this.y=e.centerY();this.y=e.right()+this.getWidth()/2};AnimatedObject.prototype.alignRight=function(e){this.y=e.centerY();this.y=e.left()-this.getWidth()/2};AnimatedObject.prototype.alignTop=function(e){this.x=e.centerX();this.y=e.top()-this.getWidth()/2};AnimatedObject.prototype.alignBottom=function(e){this.x=e.centerX();this.y=e.bottom()+this.getWidth()/2};AnimatedObject.prototype.getClosestCardinalPoint=function(e,t){var n;var r;var i;var s;if(e<this.left()){n=this.left()-e;i=this.left()}else if(e>this.right()){n=e-this.right();i=this.right()}else{n=0;i=this.centerX()}if(t<this.top()){r=this.top()-t;s=this.top()}else if(t>this.bottom()){r=t-this.bottom();s=this.bottom()}else{r=0;s=this.centerY()}if(r>n){i=this.centerX()}else{s=this.centerY()}return[i,s]};AnimatedObject.prototype.centered=function(){return false};AnimatedObject.prototype.pulseHighlight=function(e){if(this.highlighted){var t=e/7;var n=Math.abs(t%(2*this.range-2)-this.range+1);this.highlightDiff=n+this.minHeightDiff}};AnimatedObject.prototype.getTailPointerAttachPos=function(e,t,n){return[this.x,this.y]};AnimatedObject.prototype.getHeadPointerAttachPos=function(e,t){return[this.x,this.y]};AnimatedObject.prototype.identifier=function(){return this.objectID};AnimatedObject.prototype.getText=function(e){return this.label};AnimatedObject.prototype.getTextColor=function(e){return this.labelColor};AnimatedObject.prototype.setTextColor=function(e,t){this.labelColor=e};AnimatedObject.prototype.setText=function(e,t){this.label=e}